mapper.update:
		update member m
    		set m.member_point = 
        		(select sum(p.member_point_savepoint - p.member_point_usepoint)
            		from point p)
            where member_id = #{member_id}

mapper.select:
select member_point
	from member
		where member_id = #{member_id}

		select member_donation
			from member
    			where member_id = #{member_id}
